import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import axios from 'axios';

class LoginComponent extends Component {

    state = {
        emailOrPhoneNumber: '',
        password: '',
    }

    handleChange = event => {
        this.setState({ [event.target.name]: event.target.value })
    }

    handleSubmit = event => {
        event.preventDefault();
        const loginData = {
            emailOrPhoneNumber: this.state.emailOrPhoneNumber,
            password: this.state.password,
        }

        console.log(loginData);

        axios({
            method: "post",
            url: 'http://localhost:3001/UserRegistrationAndLogin/login',
            data: loginData,
        })
        .then((res)=>{
            alert('Login successfully');
        //    console.log(res.data);
         //res.redirect("http://localhost:3000/user-manage-profile")
         window.location = "/user-manage-profile";
        })
        .catch((err) =>{
            alert('failed');
            console.log(err);

        })
    }

    componentDidMount() {
        window.scrollTo(0, 0)
    }

    render() {
        return (
            <div>
                <div className="login-section">
                    <div className="container">
                        <div className="row">
                            <section className="login">
                                <form className="login-form col-md-5 mx-auto" onSubmit={this.handleSubmit}>
                                    <div className="form-group">
                                        <label>Sign In</label>

                                        <div className="form-group mt-3">
                                            <input type="text" placeholder="Enter Email Id/PhoneNumber" className="form-control" maxlength="" name="emailOrPhoneNumber" onChange={this.handleChange} />
                                        </div>
                                        <div className="form-group">
                                            <input type="password" placeholder="Enter Password" className="form-control" maxlength="" name="password" onChange={this.handleChange} />
                                        </div>

                                        <div className="form-group text-center">
                                            <button type="submit" className="btn login-btn">Sign In</button>
                                        </div>


                                        <p className="text-white">Don't Have An Account ? <Link to="register" className="text-white"> Join Now</Link></p>
                                    </div>
                                </form>
                            </section>

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default LoginComponent;
