import React, { Component } from "react";
import axios from "axios";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import {GoogleLogin} from 'react-google-login';


class RegisterComponent extends Component {
  state = {
    email: "",
     phoneNumber: "",
    password: "",
  };

  responseSuccessGoogle=(res)=>{
    const tokenData={token:res.tokenId}
    console.log(tokenData);
    axios.post("http://localhost:3001/api/googleLogin",res.tokenId,
      {
        headers: {'Content-Type':'application/x-www-form-urlencoded'},     
    
      })
    
      .then((res) => {
        console.log(res);
        alert("signup is done");
        //  console.log(res.data);
        window.location = "/user-manage-profile";
      }).catch(err=>{
        console.log(err)
      })
     // console.log(res.tokenId);
}
responseErrorGoogle=(res)=>{
   console.log(res)
 }
  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const loginData = {
      email: this.state.email,
      phoneNumber: this.state.phoneNumber,
      password: this.state.password,
    };

    // componentDidMount() {
    //     window.scrollTo(0, 0)
    //   }

    axios({
      method: "post",
      url: "http://localhost:3001/UserRegistrationAndLogin/signup",
      data: loginData,
    })
      .then((res) => {
        console.log(res);
        alert("signup is done");
        //  console.log(res.data);
        window.location = "/login";
      })
      .catch((err) => {
        alert("oops Registration is failed");

        console.log(err);
      });
  };
  render() {
    return (
      <div>
        <div className="login-section">
          <div className="container">
            <div className="row">
              <section className="login">
                <form
                  className="login-form col-md-5 mx-auto"
                  onSubmit={this.handleSubmit}
                >
                  <div className="form-group">
                    <label>Register</label>
                    <p>
                      Your details are safe with us, <br /> We will never share
                      it.
                    </p>
                    <div className="form-group">
                      <input
                        type="text"
                        placeholder="Enter Phone"
                        className="form-control"
                        maxlength=""
                        name="phoneNumber"
                        onChange={this.handleChange}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="email"
                        placeholder="Enter Email Id"
                        className="form-control"
                        maxlength=""
                        name="email"
                        onChange={this.handleChange}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="password"
                        placeholder="Enter Password"
                        className="form-control"
                        maxlength=""
                        name="password"
                        onChange={this.handleChange}
                      />
                    </div>

                    <div className="form-group text-center">
                      <button type="submit" className="btn login-btn">
                        Register
                      </button>
                    </div>

                    {/* <div className="enter-otp login-process-box d-custom-none">
                                            <input type="text" placeholder="999999" className="form-control" maxlength="6" />
                                            <div className="text-center">
                                                <p className="resend-otp">Resend OTP</p>
                                                <button type="button" className="btn login-btn" id="enterOtpBtn" disabled>Enter OTP</button>
                                            </div>
                                        </div> */}

                    {/* <div className="create-mpin login-process-box d-custom-none">
                                            <input type="text" placeholder="9999" className="form-control" maxlength="4" />
                                            <div className="text-center">
                                                <button type="button" className="btn login-btn" onclick="window.open('user-manage-profile','_self')">Create MPIN</button>
                                            </div>
                                        </div> */}
                    <p className="text-center">OR</p>

                    <div className="form-group text-center">
                      <GoogleLogin
                        clientId="992991854071-nds54jj1itcd88f3fd8796ogn1dpbsjr.apps.googleusercontent.com"
                        onSuccess={this.responseSuccessGoogle}
                        onFailure={this.responseErrorGoogle}
                        buttonText="SignUp-With-Google"
                      />
                    </div>

                    <p className="text-white">
                      Have An Account? Click here to{" "}
                      <Link to="login" className="text-white">
                        {" "}
                        Sign In
                      </Link>
                    </p>

                    {/* <p className="text-white"><i className="fa fa-gift"></i> Get Rewards, Gifts</p> */}
                  </div>
                </form>
              </section>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default RegisterComponent;
